mapper.select:
SELECT invalid as infoSysInaccess, sysNetworkUnreach, sysURLUnreach, (invalid-sysNetworkUnreach-sysURLUnreach) as otherUnreach FROM ( select count(distinct mosn) as invalid from tcmo where fmotype like "ap%" and mosn in ( select mosn from tcapct where ctid in ( select fctid from tmct where mosn in ( select distinct mosn from tfactive where fcause in ('拨测连接失败','URL应答码错误','拨测未知错误','拨测连接超时','域名解析错误')
) ) ) )unreach, ( SELECT SUM( CASE WHEN fip='网络不通' THEN num ELSE 0 END) as sysNetworkUnreach , SUM( CASE WHEN fip='URL错误' THEN num ELSE 0 END) as sysURLUnreach FROM ( SELECT tt.fcause AS fip,SUM(tt.num) AS num FROM (SELECT COUNT(*) AS num, CASE WHEN tf.fcause="设备不可达" THEN "网络不通" WHEN tf.fcause="拨测连接失败" THEN "网络不通" WHEN tf.fcause="拨测连接超时" THEN "网络不通" WHEN tf.fcause="拨测未知错误" THEN "网络不通" WHEN tf.fcause="URL应答码错误" THEN "URL错误" else '其它' END fcause FROM tfactive tf, ( SELECT mosn FROM tcmo WHERE fmotype like "ap%" UNION (SELECT tm.mosn FROM tmct tm ,(SELECT tc.ctid FROM tcapct tc,tcmo t WHERE t.fmotype like "ap%" AND t.mosn = tc.MOSN)tt WHERE tm.fCtID = tt.ctid))th WHERE
tf.fSeverity=5 AND th.mosn = tf.mosn GROUP BY fcause )tt GROUP BY fcause )reason )unreachReason
